/*==================================================
src/components/Home.js

The Home component is used to demonstrate the use of Link.
==================================================*/
import React, {Component} from 'react';
import AccountBalance from './AccountBalance';
import {Link} from 'react-router-dom';

// https://picsum.photos/200/200



const Home = ({ accountBalance }) => {
  // Inline styles for the component with Montserrat font and new balance color
  const containerStyle = {
    textAlign: 'center',
    maxWidth: '600px',
    margin: '20px auto',
    padding: '20px',
    backgroundColor: '#ffffff',
    boxShadow: '0px 8px 16px rgba(0, 0, 0, 0.2)',
    borderRadius: '12px',
    fontFamily: "'Montserrat', sans-serif",  // Apply Montserrat font
  };

  const imageStyle = {
    width: '100%',
    maxWidth: '200px',
    borderRadius: '50%',   // Circular image
    marginBottom: '20px',
    border: '5px solid #6c63ff',
  };

  const headingStyle = {
    fontSize: '2.5em',
    color: '#3f3d56',
    letterSpacing: '0.05em',  // Slight letter spacing for a more open feel
  };

  const navLinksStyle = {
    display: 'flex',
    justifyContent: 'space-around',
    marginBottom: '30px',
  };

  const linkStyle = {
    color: '#0073e6',
    textDecoration: 'none',
    fontWeight: '600',
    fontSize: '1em',
    transition: 'color 0.3s ease',
  };

  const balanceStyle = {
    fontSize: '1.5em',
    fontWeight: 'bold',
    color: '#ff5722',  // Set balance color to a vibrant orange
    letterSpacing: '0.05em',  // Adjust letter spacing for an elongated look
  };

  return (
    <div style={containerStyle}>
      <img 
        src="https://picsum.photos/200/200" 
        alt="bank" 
        style={imageStyle}
      />
      <h1 style={headingStyle}>Bank of React</h1>
      <div style={navLinksStyle}>
        <a href="/userProfile" style={linkStyle}>User Profile</a>
        <a href="/login" style={linkStyle}>Login</a>
        <a href="/credits" style={linkStyle}>Credits</a>
        <a href="/debits" style={linkStyle}>Debits</a>
      </div>
      <div style={balanceStyle}>Balance: ${accountBalance.toFixed(2)}</div>
    </div>
  );
};

export default Home;
