/*==================================================
src/components/Credits.js

The Credits component contains information for Credits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import { Link } from 'react-router-dom';
import React, { useState } from 'react';

const Credits = ({ credits, addCredit, accountBalance }) => {
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (description && amount) {
      addCredit(description, amount);
      setDescription('');
      setAmount('');
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
      <h1>Credits</h1>
      <p>Account Balance: ${accountBalance.toFixed(2)}</p>

      <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
        <div>
          <label>Description</label>
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Amount</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>
        <button type="submit">Add Credit</button>
      </form>

      <h2>Credits List</h2>
      <ul>
        {credits.map((credit, index) => (
          <li key={index} style={{ padding: '10px', borderBottom: '1px solid #ddd' }}>
            <span>{credit.description}</span> - <span>${credit.amount.toFixed(2)}</span> on <span>{credit.date}</span>
          </li>
        ))}
      </ul>

      <br />
      <Link to="/">Return to Home</Link>
    </div>
  );
};

export default Credits;
