/*==================================================
src/components/Home.js

The Home component is used to demonstrate the use of Link.
==================================================*/
import React, {Component} from 'react';
import AccountBalance from './AccountBalance';
import {Link} from 'react-router-dom';


// https://picsum.photos/200/200


const Home = ({ accountBalance }) => {
  // Inline styles for the component
  const containerStyle = {
    textAlign: 'center',
    maxWidth: '600px',
    margin: '20px auto',
    padding: '20px',
    backgroundColor: '#ffffff',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
    borderRadius: '8px',
    fontFamily: " 'Playfair Display' , serif"
  };

  const imageStyle = {
    width: '100%',
    maxWidth: '250px',
    borderRadius: '12px',
    marginBottom: '20px',
  };

  const headingStyle = {
    fontSize: '2em',
    color: '#333',
  };

  const navLinksStyle = {
    display: 'flex',
    justifyContent: 'space-around',
    marginBottom: '20px',
  };

  const linkStyle = {
    color: '#0073e6',
    textDecoration: 'none',
    fontWeight: 'bold',
  };

  const balanceStyle = {
    fontSize: '1.5em',
    fontWeight: 'bold',
    color: '#28a745',
  };

  return (
    <div style={containerStyle}>
      <img 
        src="https://picsum.photos/200/200" 
        alt="bank" 
        style={imageStyle}
      />
      <h1 style={headingStyle}>Bank of React</h1>
      <div style={navLinksStyle}>
        <a href="/userProfile" style={linkStyle}>User Profile</a>
        <a href="/login" style={linkStyle}>Login</a>
        <a href="/credits" style={linkStyle}>Credits</a>
        <a href="/debits" style={linkStyle}>Debits</a>
      </div>
      <div style={balanceStyle}>Balance: ${accountBalance.toFixed(2)}</div>
    </div>
  );
};

export default Home;
