/*==================================================
src/components/Home.js

The Home component is used to demonstrate the use of Link.
==================================================*/
import React, {Component} from 'react';
import AccountBalance from './AccountBalance';
import {Link} from 'react-router-dom';


// https://picsum.photos/200/200
// src/components/Home.js


const Home = ({ accountBalance }) => {
  // Inline styles for the component
  const containerStyle = {
    textAlign: 'center',
    maxWidth: '600px',
    margin: '20px auto',
    padding: '20px',
    backgroundColor: '#ffffff',
    boxShadow: '0px 8px 16px rgba(0, 0, 0, 0.2)',
    borderRadius: '12px',
    fontFamily: "'Playfair Display', serif",
  };

  const imageStyle = {
    width: '100%',
    maxWidth: '200px',
    borderRadius: '50%',
    marginBottom: '20px',
    border: '5px solid #6c63ff',
  };

  const headingStyle = {
    fontSize: '2.5em',
    color: '#3f3d56',
    letterSpacing: '0.05em',
  };

  const navLinksStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
    marginBottom: '30px',
  };

  const linkStyle = {
    color: '#0073e6',
    textDecoration: 'none',
    fontWeight: '600',
    fontSize: '1.2em',
    transition: 'color 0.3s ease',
  };

  const balanceStyle = {
    fontSize: '1.5em',
    fontWeight: 'bold',
    color: '#b22222',
    letterSpacing: '0.02em',
  };

  return (
    <div style={containerStyle}>
      <img src="https://picsum.photos/200/200" alt="bank" style={imageStyle} />
      <h1 style={headingStyle}>Bank of React</h1>

      <div style={navLinksStyle}>
        <Link to="/userProfile" style={linkStyle}>User Profile</Link>
        <Link to="/login" style={linkStyle}>Login</Link>
        <Link to="/credits" style={linkStyle}>Credits</Link>
        <Link to="/debits" style={linkStyle}>Debits</Link>
      </div>

      <div style={balanceStyle}>
        <AccountBalance accountBalance={accountBalance} />
      </div>
    </div>
  );
};

export default Home;
