/*==================================================
src/components/Home.js

The Home component is used to demonstrate the use of Link.
==================================================*/
import React, {Component} from 'react';
import AccountBalance from './AccountBalance';
import {Link} from 'react-router-dom';

// https://picsum.photos/200/200

// src/components/Home.js



const Home = ({ accountBalance }) => {
  // Inline styles for the component with new font and colors
  const containerStyle = {
    textAlign: 'center',
    maxWidth: '600px',
    margin: '20px auto',
    padding: '20px',
    backgroundColor: '#ffffff',
    boxShadow: '0px 8px 16px rgba(0, 0, 0, 0.2)',
    borderRadius: '12px',
    fontFamily: "'Montserrat', sans-serif",
  };

  const imageStyle = {
    width: '100%',
    maxWidth: '250px',
    borderRadius: '50%',   // Circular image style
    marginBottom: '20px',
    border: '5px solid #6c63ff',  // Adds a border with a purple color
  };

  const headingStyle = {
    fontSize: '2.5em',
    color: '#333',
    marginBottom: '10px',
    color: '#3f3d56',  // Dark purple for the main heading
  };

  const subheadingStyle = {
    fontSize: '1.2em',
    color: '#6c63ff',  // Light purple accent color
    marginBottom: '20px',
  };

  const navLinksStyle = {
    display: 'flex',
    justifyContent: 'space-around',
    marginBottom: '30px',
  };

  const linkStyle = {
    color: '#0073e6',    // Bright blue for links
    textDecoration: 'none',
    fontWeight: 'bold',
    fontSize: '1em',
    transition: 'color 0.3s ease',
  };

  const linkHoverStyle = {
    color: '#5a0ecc',    // Slightly darker color on hover
  };

  const balanceStyle = {
    fontSize: '1.5em',
    fontWeight: 'bold',
    color: '#28a745',   // Green for the balance to signify positivity
  };

  return (
    <div style={containerStyle}>
      <img 
        src="https://picsum.photos/200/200" 
        alt="bank" 
        style={imageStyle}
      />
      <h1 style={headingStyle}>Bank of React</h1>
      <p style={subheadingStyle}>Your trusted digital banking experience</p>
      <div style={navLinksStyle}>
        <a href="/userProfile" style={linkStyle}>User Profile</a>
        <a href="/login" style={linkStyle}>Login</a>
        <a href="/credits" style={linkStyle}>Credits</a>
        <a href="/debits" style={linkStyle}>Debits</a>
      </div>
      <div style={balanceStyle}>Balance: ${accountBalance.toFixed(2)}</div>
    </div>
  );
};

export default Home;
