/*==================================================
src/components/UserProfile.js

The UserProfile component is used to demonstrate the use of Route and Link.
Note: You don't need to work on this file for the Assignment.
==================================================*/
import React, {Component} from 'react';
import {Link} from 'react-router-dom';

const UserProfile = ({ userName, memberSince }) => {
  // Inline styles for the component
  const containerStyle = {
    textAlign: 'center',
    maxWidth: '500px',
    margin: '20px auto',
    padding: '20px',
    backgroundColor: '#ffffff',
    boxShadow: '0px 8px 16px rgba(0, 0, 0, 0.2)',
    borderRadius: '12px',
    fontFamily: "'Playfair Display', serif",  // Consistent font style
  };

  const headingStyle = {
    fontSize: '2em',
    color: '#3f3d56',  // Dark purple for headings
    marginBottom: '20px',
  };

  const infoStyle = {
    fontSize: '1.2em',
    color: '#333',
    marginBottom: '10px',
  };

  const labelStyle = {
    fontWeight: 'bold',
    color: '#6c63ff',  // Purple accent color for labels
  };

  const linkStyle = {
    color: '#0073e6',
    textDecoration: 'none',
    fontWeight: 'bold',
    marginTop: '20px',
    display: 'inline-block',
  };

  return (
    <div style={containerStyle}>
      <h1 style={headingStyle}>User Profile</h1>
      <p style={infoStyle}>
        <span style={labelStyle}>Username:</span> {userName}
      </p>
      <p style={infoStyle}>
        <span style={labelStyle}>Member Since:</span> {memberSince}
      </p>
      <a href="/" style={linkStyle}>Return to Home</a>
    </div>
  );
};

export default UserProfile;
