/*==================================================
src/components/Debits.js

The Debits component contains information for Debits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
// src/components/Debits.js

import React, { useState } from 'react';

const Debits = ({ debits, addDebit, accountBalance }) => {
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!description || !amount) return; // Basic validation
    addDebit(description, parseFloat(amount));
    setDescription('');
    setAmount('');
  };


  return (
    <div className="App">
      <h1>Debits</h1>
      <div className="balance">Account Balance: ${accountBalance.toFixed(2)}</div>

      <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
        <input 
          type="text" 
          placeholder="Description" 
          value={description} 
          onChange={(e) => setDescription(e.target.value)} 
        />
        <input 
          type="number" 
          placeholder="Amount" 
          value={amount} 
          onChange={(e) => setAmount(e.target.value)} 
        />
        <button type="submit">Add Debit</button>
      </form>

      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {debits.map((debit, index) => (
          <li key={index} style={{ marginBottom: "10px" }}>
            <strong>{debit.description}</strong> - ${debit.amount.toFixed(2)} <br />
            Date: {debit.date}
          </li>
        ))}
      </ul>

      <a href="/">Return to Home</a>
    </div>
  );
};

export default Debits;
